var path=require('path');
const origin='.'+path.sep+'screenShots'+path.sep+'my_ui_reference'+path.sep;
const test='.'+path.sep+'screenShots'+path.sep+'my_ui_test'+path.sep;
const fs = require('fs');
const diff = '.'+path.sep+'screenShots'+path.sep+'my_ui_diff'+path.sep;

const report =function(boolean)
{
	if(boolean)
	{
		var imageList={
			"origin":[],
			"test":[],
			"diff":[]
		};

		fs.readdirSync(origin).forEach(function(file){
			imageList.origin.push(file)
		});

		fs.readdirSync(test).forEach(function(file){
			imageList.test.push(file)
		});

		fs.readdirSync(diff).forEach(function(file){
			imageList.diff.push(file)
		});

		var list = "var fileObj="+JSON.stringify(imageList);

		fs.writeFile('./screenShots/detail.js',list, (err) => {
			if(err)
			{
			 	throw err;
			} 
		});
	}
	
}

module.exports = {
	report
}